<template>
  <v-container class="py-8">
    <v-row>
      <v-col cols="12">
        <h1 class="text-center py-14">Kontakt & Anfahrt</h1>
      </v-col>
    </v-row>
    <v-row justify="center" align="center">
      <v-col cols="12" lg="6" class="my-auto">
        <Info class="mb-3" />
        <OpeningHours class="mt-3" />
      </v-col>
      <v-divider :vertical="!$vuetify.breakpoint.mobile"></v-divider>
      <v-col cols="12" lg="6" class="my-auto">
        <GMap
          ref="gMap"
          :language="$i18n.locale"
          :cluster="{ options: { styles: clusterStyle } }"
          :center="{ lat: locations[0].lat, lng: locations[0].lng }"
          :options="{ fullscreenControl: false, styles: mapStyle }"
          :zoom="13"
        >
          <GMapMarker
            v-for="location in locations"
            :key="location.id"
            :position="{ lat: location.lat, lng: location.lng }"
            :options="{
              icon:
                location === currentLocation ? pins.selected : pins.notSelected,
            }"
            @click="currentLocation = location"
          >
            <GMapInfoWindow :options="{}">
              <v-btn plain :href="googleMapsLink(location)" target="_blank">
                In Google Maps ansehen
              </v-btn>
            </GMapInfoWindow>
          </GMapMarker>
          <GMapCircle :options="circleOptions" />
        </GMap>
      </v-col>
    </v-row>
  </v-container>
</template>
<script lang="ts">
import Vue from "vue"
export default Vue.extend({
  data() {
    return {
      currentLocation: {},
      circleOptions: {},
      locations: [
        {
          lat: 52.17265,
          lng: 13.661811,
          address: "Am Hornungsee 4, 15746 Groß Köris",
        },
      ],
      pins: {
        selected:
          "data:image/png;base64,",
        notSelected:
          "data:image/png;base64,",
      },
      mapStyle: [],
      clusterStyle: [
        {
          url: "https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m1.png",
          width: 56,
          height: 56,
          textColor: "#fff",
        },
      ],
    }
  },
  methods: {
    googleMapsLink(location: any) {
      return "http://maps.google.com/maps?daddr=" + location.address
    },
  },
})
</script>

